var Ata = (function (exports) {
	'use strict';

	var config = {
	  sections: {
	    sectionsHook: "[data-section]",
	    sectionsStateClass: "is-visible"
	  },
	  header: {
	    navTriggerHook: ".hamburger",
	    navTriggerStateClass: "is-active",
	    navStateClass: "nav-is-open"
	  },
	  mapSearchBox: {
	    containerClass: ".mp-SearchBox",
	    searchBtnClass: ".mp-SearchBox_BtnSearch",
	    cancelBtnClass: ".mp-SearchBox_BtnCancel"
	  },
	  keywords: {
	    containerId: "#js-Keywords",
	    listClassName: "mp-Keywords_List",
	    itemClassName: "mp-Keywords_Item",
	    labelClassName: "mp-Keywords_Label",
	    btnClassName: "o-btn mp-Keywords_BtnDelete"
	  },
	  markers: {
	    size: {
	      xs: 34,
	      s: 44,
	      m: 54,
	      l: 64,
	      xl: 74,
	      xxl: 84
	    }
	  }
	};

	// ==================================

	var handleClickEvent = function handleClickEvent(nodeList) {
	  var total = nodeList.length;

	  var _loop = function _loop(i) {
	    var trigger = nodeList[i];
	    var triggerStateClass = config.header.navTriggerStateClass; // Identifier l'autre bouton (sur un total de 2)
	    // afin de gérer son état également lors du clic

	    var i_alt = (i + 1) % total;
	    trigger.addEventListener("click", function () {
	      var open = navState();

	      if (open) {
	        trigger.classList.add(triggerStateClass);
	        nodeList[i_alt].classList.add(triggerStateClass);
	      } else {
	        trigger.classList.remove(triggerStateClass);
	        nodeList[i_alt].classList.remove(triggerStateClass);
	      }
	    }, false);
	  };

	  for (var i = 0; i < total; i++) {
	    _loop(i);
	  }
	};

	var navState = function navState() {
	  var body = document.body;
	  var state;
	  var navStateClass = config.header.navStateClass;

	  if (body.classList.contains(navStateClass)) {
	    body.classList.remove(navStateClass);
	    state = false;
	  } else {
	    body.classList.add(navStateClass);
	    state = true;
	  }

	  return state;
	};

	var navInit = function navInit() {
	  var navTriggers = document.querySelectorAll(config.header.navTriggerHook);
	  handleClickEvent(navTriggers);
	};

	// ================================================

	var pageInit = function pageInit() {
	  var pageSections = document.querySelectorAll(config.sections.sectionsHook);
	  var sectionClass = config.sections.sectionsStateClass;
	  setTimeout(function () {
	    for (var i = 0; i < pageSections.length; i++) {
	      var section = pageSections[i];
	      section.classList.add(sectionClass);
	    }
	  }, 800);
	};

	// import { addKeyword } from "./map/keywords";
	// Initialiser les scripts relatifs au site

	var init = function init() {
	  navInit();
	  pageInit();
	}; // Initialiser le module relatif à l'atlas
	// export const map = (mapObj) => {
	// 	mapInit(mapObj);
	// }
	// export { addKeyword };

	exports.init = init;

	return exports;

}({}));
